import React from 'react';
import { NavLink } from 'react-router-dom';

import HeaderItem from '../HeaderItem/HeaderItem';

import styles from './Header.module.css';

import { connect } from 'react-redux';

function get_links(authorizationFlag) {
	return [
		{url: '/authorization', text: "Авторизация", active: !authorizationFlag},
		{url: '/appointment/department', text: "Записатьcя", active: authorizationFlag},
		{url: '/appointments', text: "Мои записи", active: authorizationFlag},
		{url: '/help', text: "Помощь", active: true},
		{url: '/exit', text: "Выход", active: authorizationFlag},
	]
}

	// componentDidUpdate(prevProps, prevState, snapshot) {
	// 	if (prevProps.active !== this.props.active) {
	// 		this.setState(this.stateFromProps(this.props));
	// 	}
	// }

function render({authorizationFlag}) {
	console.log('authorization', this.props)
	return (
		<div className={styles.board}>
			{this.get_links(authorizationFlag).map(link =>
				link['active'] &&
				<NavLink to={link['url']} className={styles.link} activeClassName={styles.activeItem}> 
					<HeaderItem text={link['text']} />
			</NavLink>)}
		</div>
	);
}

const mapStateToProps = state => ({authorizationFlag: state.authorization.authorizationFlag});

export { render };

export default connect(mapStateToProps)(Header);
