import React from 'react';
import { connect } from 'react-redux';

import Tile from '../components/Tile/Tile'

import styles from './AppointmentPage.module.css'

import Spinner from 'react-bootstrap/Spinner'

import { loadAppointment, deleteAppointment } from '../redux/appointment/actions';
import store from '../redux/store/store';

class AppointmentPage extends React.Component{
	constructor(props) {
		super(props);
		this.handleAppointment = this.handleAppointment.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
		this.handleDeleteAllAppointment = this.handleDeleteAllAppointment.bind(this)
	};

	handleAppointment() {
		this.props.loadAppointment([this.props.omsNumber, this.props.birthDate]);
	}

	handleDelete(id) {
		this.props.deleteAppointment([[id], this.props.omsNumber, this.props.birthDate]);
	}

	handleDeleteAllAppointment(indexDoctor) {
		this.props.deleteAppointment([
			this.props.doctors[indexDoctor].map(x=>x.appointment_id),
			this.props.omsNumber,
			this.props.birthDate,
		]);
	}

	//this.state.departments.filter(department => department.available_resource_id === doctor).map(department =>

	componentDidMount() {
		this.handleAppointment();
	}

	render() {
		console.log('apppointage_render', JSON.parse(JSON.stringify(store.getState())));
		const loadPage = <div className={styles.loadPage} ><Spinner animation="border" variant="primary" /></div>;

		if (this.props.loadingFlag) {
			return loadPage;
		}
		const emptyPage = <h2>{'У вас пока нет записей!'}</h2>;
		const page = (
			<div className={styles.page}>
			<ul className={styles.list}>
				{this.props.doctors
					.map((doctor, index) => 
						<Tile
							key={index}
							handleDelete={() => this.handleDeleteAllAppointment(index)}
							handleDeleteItem={this.handleDelete}
							appointments={doctor}
						/>)
				}
			</ul>
		</div>
		);
		console.log('apppointage_render_end', JSON.parse(JSON.stringify(store.getState())));
		return  this.props.doctors.length === 0 ? emptyPage : page;
	};
};


const mapStateToProps = state => ({
	doctors: [...new Set(state.appointment.departments.map(x=>x.available_resource_id))].map(x => state.appointment.departments.filter(y=>y.available_resource_id===x)),
	loadingFlag: state.appointment.loadingFlag,
	omsNumber: state.authorization.omsNumber,
	birthDate: state.authorization.birthDate,
});


const mapDispatchToProps = dispatch => ({
	loadAppointment: payload => dispatch(loadAppointment(payload)),
	deleteAppointment: payload => dispatch(deleteAppointment(payload)),
});

export { AppointmentPage };

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(AppointmentPage);
