import React, { Component, Fragment } from 'react';

import ScheduleCell from '../ScheduleCell/ScheduleCell';
import Button from '../Button/Button';

import styles from './ScheduleTable.module.css';


export default class ScheduleTable extends React.Component {
  constructor(props) {
    super(props);

    this.handleClick = this.handleClick.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // handleClick(element) {
  //   console.log(this.state)
  //   this.setState({...this.state, [element.date]: null})

  //   console.log(element);
  // };

  handleSubmit() {
    console.log('fetch', this.state)
    // {
    //   "appointment": {
    //     "available_resource_id": "10064025",
    //     "complex_resource_id": "10059868",
    //     "finish_time": "2019-12-10T14:00:00Z",
    //     "lpu_id": "10000401",
    //     "priority": 0,
    //     "reception_type_code_or_ldp_type_code": "1863",
    //     "start_time": "2019-12-10T12:00:00Z"
    //   },
    //   "person": {
    //     "birth_date": "2019-12-08",
    //     "oms_number": "string"
    //   }
    // }
  }

  render() {
    const rows = [];
    for (let i = 0; i < this.props.schedule.length / 7; i++) {
      rows[i] = []
      for (let j = 0; j <  7; j++) {
        rows[i].push(this.props.schedule[i*7+j])
      }
    }
    console.log(this.props.schedule)
    return (
      <form>
        <table className={styles.items}>
          <thead>
            <tr>
              <th>Понедельник</th>
              <th>Вторник</th>
              <th>Среда</th>
              <th>Четверг</th>
              <th>Пятница</th>
              <th>Суббота</th>
              <th>Воскресенье</th>
            </tr>
          </thead>
        <tbody>
          {rows.map(row => <tr>{row.map(col => <td><ScheduleCell handleClick={this.handleClick} cell={col}/></td>)}</tr>)}
        </tbody>
      </table>
      <Button onClick={this.handleSubmit}>{'Встать в очередь'}</Button>
    </form>
    )
  };
}


