import React from 'react';
import { NavLink } from 'react-router-dom';

import HeaderItem from '../HeaderItem/HeaderItem';

import styles from './Header.module.css';


export default class Header extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			links: [
				{url: '/authorization', text: "Авторизация", active: true},
				{url: '/appointment/department', text: "Записатьcя", active: Boolean(localStorage.getItem('schedule'))},
				{url: '/appointments', text: "Мои записи", active: Boolean(localStorage.getItem('schedule'))},
				{url: '/help', text: "Помощь", active: true},
			]
		}
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		if (prevState !== this.state) {
			this.setState({links: [
				this.state.links[0],
				Object.assign(this.state.links[1], {active: Boolean(localStorage.getItem('schedule'))}),
				Object.assign(this.state.links[2], {active: Boolean(localStorage.getItem('schedule'))}),
				this.state.links[3],
			]});
		}
	}

	render() {
		return (
			<div className={styles.board}>
				{this.state.links.map(link =>
					<NavLink to={link['active'] ? link['url'] : '#'} className={styles.link} activeClassName={link['active'] ? styles.activeItem : styles.activeItemNoValid}> 
						<HeaderItem text={link['text']} />
				</NavLink>)}
			</div>
		);
	}
}
