import React from 'react';

import styles from './Schedule.module.css';

import Department from './Department/Department';
import Doctor from './Doctor/Doctor';
import ScheduleFrame from './ScheduleFrame/ScheduleFrame';

const BASE_URL = "https://proxy-q-doctor.herokuapp.com/api/emias/"
const DEPARTMENT_URL = "departments"
const DOCTOR_URL = "doctors/"
const SCHEDULE_URL = "schedule"
const CACHE_URL = "check_cache"


export default class Schedule extends React.Component {
	constructor(props) {
		super(props);

		this.state = {departments: [], doctors: [], schedule: [], department: '', doctor: ''}

		this.handleClickDepartment = this.handleClickDepartment.bind(this);
		this.handleClickDoctor = this.handleClickDoctor.bind(this);
	}

	fetchDepartments(oms_number, birth_date) {
		return fetch(BASE_URL+DEPARTMENT_URL, {method: 'GET', body: JSON.stringify({birth_date, oms_number})})
						.then(response => response.json())
						.then(response => this.setState({ ...this.state,
																						 departments: response,
																						}));
	}

	fetchCacheCheck(oms_number, birth_date) {
		fetch(BASE_URL+CACHE_URL, {method: 'GET', body: JSON.stringify({birth_date, oms_number})})
	}

	fetchDoctors(oms_number, birth_date, speciality_id) {
		return fetch(BASE_URL+DOCTOR_URL+speciality_id, {method: 'GET', body: JSON.stringify({birth_date, oms_number})})
						.then(response => response.json())
						.then(response => this.setState({...this.state, doctors: response}));
	}

	fetchSchedule(doctor) {
		const available_resource_id = doctor['lpuRoomsInfo']['lpuRoomView'][0]['availableResourceId']
		const lpu_id = doctor['lpuRoomsInfo']['lpuRoomView'][0]['lpuId']

		return fetch(BASE_URL+SCHEDULE_URL, {method: 'GET', body: JSON.stringify({available_resource_id, lpu_id})})
						.then(response => response.json())
						.then(response => this.setState({...this.state, scheduleCache: response}));
	}

	componentDidMount() {
		this.fetchDepartments(this.props.omsNumber, this.props.birthDate);
		this.fetchCacheCheck(this.props.omsNumber, this.props.birthDate);
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		if (this.state.department !== prevState.department) {
			this.fetchDoctors(this.props.omsNumber, this.props.birthDate, this.state.department);
		}

		if (this.state.doctor !== prevState.doctor) {
			// console.log('Doctors: ', this.state.doctor, prevState.doctor)
			// console.log(prevState.doctor)
			// console.log(this.state.doctor)
			this.doctorFromSchedule(this.fetchSchedule(this.state.lpuId), this.state.doctor);
		}
	}

	handleClickDepartment(element) {
		if (this.state.department !== element) {
			this.setState({...this.state, department: element, doctors: [], schedule: []})}
	}

	handleClickDoctor(element) {
		if (this.state.doctor !== element) {
			this.setState({...this.state, doctor: element, schedule: []})}
	}

  render() {
		return (
			<div className={styles.schedule}>
				<div className={styles.department}>
					<Department items={this.state.departments} handleClick={this.handleClickDepartment} />
				</div>
				{this.state.doctors.length !== 0 &&
					<div className={styles.doctor}>
						<Doctor items={this.state.doctors} handleClick={this.handleClickDoctor} />
					</div>}
				{this.state.schedule.length !== 0 &&
					<div className={styles.scheduleFrame}>
						<ScheduleFrame items={this.state.schedule} />
					</div>}
			</div>
		);
	};
};
