import React from 'react';
import { NavLink } from 'react-router-dom';

import ScheduleCell from '../ScheduleCell/ScheduleCell';

import Button from 'react-bootstrap/Button'

import styles from './ScheduleTable.module.css';

import { createAppointment } from '../../redux/appointment/actions';
import store from '../../redux/store/store'


function days2weeks(days) {
  const weeks = [];

  for (let i = 0; i < days.length / 7; i++) {
    weeks[i] = []
    for (let j = 0; j <  7; j++) {
      weeks[i].push(days[i*7+j])
    }
  }

  return weeks
}


export default class ScheduleTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.handleClick = this.handleClick.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleClick(element) {
    this.setState({[element.date]: !this.state[element.date]});
  }

  handleSubmit() {
    store.dispatch(createAppointment([
      store.getState().authorization.omsNumber,
      store.getState().authorization.birthDate,
      this.props.availableResourceId,
      this.props.complexResourceId,
      this.props.lpuId,
      this.props.code,
      Object.keys(this.state).filter(x => this.state[x])
    ]
    ));
    this.setState(Object.keys(this.state).reduce((obj, x) => {obj[x] = false; return obj;}, {}))
  }

  renderHead() {
    return (
      <thead>
        <tr>
          <th>Понедельник</th>
          <th>Вторник</th>
          <th>Среда</th>
          <th>Четверг</th>
          <th>Пятница</th>
          <th>Суббота</th>
          <th>Воскресенье</th>
        </tr>
      </thead>);
  }

  renderBody() {
    const table = [];
    const weeks = days2weeks(this.props.schedule);

    for (const week of weeks){
      let row = [];
      for (const day of week) {
        // active={Boolean(this.state[day.date])}
        row.push(<td><ScheduleCell handleClick={this.handleClick} active={Boolean(this.state[day.date])} cell={day}/></td>)
      }
      table.push(<tr>{row}</tr>)
    }

    return table;
  }

  render() {
    return (
      <div className={styles.schedule}>
        <table className={styles.table}>
          {this.renderHead()}
        <tbody>
          {this.renderBody()}
        </tbody>
      </table>
      <div className={styles.footer}>
        <NavLink to={'/appointments'}>
          <Button onClick={this.handleSubmit} variant="primary" size="lg" active>{'Встать в очередь'}</Button>
        </NavLink>
      </div>
    </div>
    );
  }

}
