import React from 'react';

import styles from './Tile.module.css';

import TileItem from '../TileItem/TileItem';

const BASE_URL = "http://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"


export default class Tile extends React.Component {
	constructor(props) {
		super(props);

		// this.state = {appointments: []}
		this.handleSubmit.bind(this);
	}

	fetchDeleteAppointment(appointmentId, omsNumber, birthDate) {
		return fetch(BASE_URL+APPOINTMENT_URL+`/${appointmentId}?oms_number=${omsNumber}&birth_date=${birthDate}`)
	}

	handleSubmit(appointmentId) {
		this.fetchDeleteAppointment(appointmentId, localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'))
	}

	// componentDidMount() {}

  render() {
		const departments = JSON.parse(localStorage.getItem('schedule'))['departments']
		const doctors = Object.assign(...Object.values(departments).map(x => x['doctors']))
		const metaDoctor = doctors[this.props.appointments[0].available_resource_id]

		return (
      <li className={styles.tile}>
        <h3>{metaDoctor.firstName + '\t' + metaDoctor.lastName}</h3>
        <ul className={styles.list}>
          {this.props.appointments.map(record => <TileItem key={record.appointment_id} {...record} handleSubmit={() => this.handleSubmit(record.appointment_id)} />)}
        </ul>
      </li>
		);
	};
};
