export default (state=[], action) => {
	switch (action.type) {
		case 'INIT_DEPARTMENTS':
      return {schedule: {'departments': []}, textError: '', loadingFlag: false};

		case 'LOAD_DEPARTMENTS':
			return {schedule: {'departments': []}, textError: '', loadingFlag: true};

		case 'LOAD_DEPARTMENTS_FAILURE':
			return {schedule: {'departments': []}, textError: action.payload, loadingFlag: false};

		case 'LOAD_DEPARTMENTS_SUCCESS':
			return {schedule: action.payload, textError: '', loadingFlag: false};

		case 'RESET_DEPARTMENTS':
			return {schedule: {'departments': []}, textError: '', loadingFlag: false};

		default:
      return state;
	}
};
