import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';

import ScheduleCell from '../ScheduleCell/ScheduleCell';

import Button from 'react-bootstrap/Button'

import styles from './ScheduleTable.module.css';

import { createAppointment } from './redux/appointment/actions';


function days2weeks(days) {
  const weeks = [];

  for (let i = 0; i < days.length / 7; i++) {
    weeks[i] = []
    for (let j = 0; j <  7; j++) {
      weeks[i].push(days[i*7+j])
    }
  }

  return weeks
}


class ScheduleTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  handleClick(element) {
    this.setState(Object.assign(this.state, {[element.date]: !this.state[element.date]}))
  };

  handleSubmit() {
    const appointments = [];
    for (let key of Object.keys(this.state).filter(key => this.state[key])) {
      const start = new Date(Date.parse(key));
      const end = new Date(new Date(start).setDate(start.getDate()+1))
      appointments.push({
        "appointment": {
          "available_resource_id": String(this.props.availableResourceId),
          "complex_resource_id": String(this.props.complexResourceId),
          "start_time": start.toISOString().split('.')[0],
          "finish_time": end.toISOString().split('.')[0],
          "lpu_id": String(this.props.lpuId),
          "priority": 0,
          "reception_type_code_or_ldp_type_code": this.props.code,
        },
        "person": {
          "birth_date": localStorage.getItem('birthDate'),
          "oms_number": localStorage.getItem('omsNumber'),
        }
      })
    }
    this.setState({}); // todo question!!!!
    this.fetchCreateAppointment(appointments)    
  }

  renderHead() {
    return (
      <thead>
        <tr>
          <th>Понедельник</th>
          <th>Вторник</th>
          <th>Среда</th>
          <th>Четверг</th>
          <th>Пятница</th>
          <th>Суббота</th>
          <th>Воскресенье</th>
        </tr>
      </thead>);
  }

  renderBody() {
    const table = [];
    const weeks = days2weeks(this.props.schedule);

    for (const week of weeks){
      let row = [];
      for (const day of week) {
        // active={Boolean(this.state[day.date])}
        row.push(<td><ScheduleCell handleClick={this.handleClick} active={Boolean(this.state[day.date])} cell={day}/></td>)
      }
      table.push(<tr>{row}</tr>)
    }

    return table;
  };

  render() {
    return (
      <div className={styles.schedule}>
        <table className={styles.table}>
          {this.renderHead()}
        <tbody>
          {this.renderBody()}
        </tbody>
      </table>
      <div className={styles.footer}>
        <Button onClick={this.handleSubmit} variant="primary" size="lg" active>{'Встать в очередь'}</Button>
      </div>
    </div>
    );
  };

}

const mapStateToProps = state => ({shedule: state.schedule.schedule})

const mapDispatchToProps = dispatch => ({
  createAppointment: payload => dispatch(createAppointment(payload)),
});


export { ScheduleTable };

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ScheduleTable);
