import React from 'react';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import Spinner from 'react-bootstrap/Spinner';

import LinkItem from '../components/LinkItem/LinkItem'

import styles from './DefaultPage.module.css'

import { loadDoctors } from '../redux/emias/actions';

class DoctorPage extends React.Component {
  handleDoctor () {
    this.props.loadDoctors([this.props.omsNumber, this.birthDate, this.props.match.params.departmentId])
  }

  componentDidMount() {
    this.handleDoctor()
  }

  renderPage() {
    return (
      <div className={styles.page}>
        <ul className={styles.list}>
          {this.props.doctors.map(doctor =>
            <NavLink key={doctor.id} to={`doctor/${doctor.id}/schedule`} >
              <LinkItem
                key={doctor.id}
                index={doctor.id}
                text={doctor.lastName + ' ' + doctor.firstName + ' ' + doctor.secondName} />
            </NavLink>)
          }
        </ul>
      </div>
    );
  }

  renderLoadPage = () =>  (
    <div className={styles.loadPage} ><Spinner animation='border' variant='primary' /></div>
  );

  render() {
    return this.props.loadingFlag ? this.renderLoadPage() : this.renderPage();
  }
}


const mapStateToProps = state => (
  {
    doctors: state.emias.doctors,
    loadingFlag: state.emias.doctors === undefined,
    omsNumber: state.user.omsNumber,
    birthDate: state.user.birthDate,
  }
);

const mapDispatchToProps = dispatch => ({
	loadDoctors: payload => dispatch(loadDoctors(payload)),
});

export default connect(mapStateToProps, mapDispatchToProps)(DoctorPage);
