import React from 'react';
import TimeRangeSlider from 'react-time-range-slider';

import styles from './ScheduleCell.module.css';

const info2time = (info) => {
	const array = info.split(', ').flatMap(period => period.split('-'))
	const min = array.reduce((min, elem) => (parseInt(min.replace(':', '')) > parseInt(elem.replace(':', '')) ? elem : min), '23:59')
	const max = array.reduce((min, elem) => (parseInt(min.replace(':', '')) < parseInt(elem.replace(':', '')) ? elem : min), '00:00')
	console.log(min, max)
	return [min, max];
}

export default class ScheduleCell extends React.Component {
// 	constructor(props) {
// 		super(props);
// 		this.featureRef = React.createRef();
// 		this.changeStartHandler = this.changeStartHandler.bind(this);
// 		this.timeChangeHandler = this.timeChangeHandler.bind(this);
// 		this.changeCompleteHandler = this.changeCompleteHandler.bind(this);
// }
	constructor(props) {
		super(props);
		this.timeChangeHandler = this.timeChangeHandler.bind(this);
		this.state = {
			active: this.props.active, 
			value: {start: "00:00", end: "23:59"}
		};
	}

	changeStyle() {
		this.setState({active: !this.state.active})
	}

	timeChangeHandler(time){
		this.setState({
				value: time
		});
	}

	render() {
		if (this.props.cell) {
			if (['Отпуск', 'Заболел'].includes(this.props.cell['receptionInfo'])) {
				return (
					<div className={styles.colEmpty}>
						<div className={styles.date}>
							{this.props.cell['date']}
						</div>
						<div className={styles.time}>
							{this.props.cell['receptionInfo']}
						</div>
					</div>
				);
			} else if (this.props.cell['receptionInfo']) {
				const [start, end] = info2time(this.props.cell['receptionInfo'])
				return (
					<div 
						className={this.state.active ? styles.colAcive : styles.col}
						onClick={() => {this.props.handleClick(this.props.cell); this.changeStyle()}}>
						<div className={styles.date}>
							{this.props.cell['date']}
						</div>
						<div className={styles.time}>
						<TimeRangeSlider
                disabled={false}
                format={24}
                maxValue={end}
                minValue={start}
                name={"time_range"}
                // onChangeStart={this.changeStartHandler}
                // onChangeComplete={this.changeCompleteHandler}
                onChange={this.timeChangeHandler}
                step={15}
                value={this.state.value}/>
						</div>
					</div>
				);
			} else {
				return (
					<div className={styles.colNotAllowed}>
						<div className={styles.date}>
							{this.props.cell['date']}
						</div>
					</div>
				);
			}
		} else {
				return <div className={styles.colEmpty} ></div>
		}
	}

}
