export default (state=undefined, action) => {
	switch (action.type) {
		case 'INIT_APPOINTMENT':
      return {departments: action.payload, loadingFlag: false};

		case 'LOAD_APPOINTMENT':
			return {departments: [], loadingFlag: true};

		case 'LOAD_APPOINTMENT_SUCCESS':
			return {departments: action.payload, loadingFlag: false};

		case 'DELETE_APPOINTMENT':
			return {departments: state.departments, loadingFlag: true};

		case 'DELETE_APPOINTMENT_SUCCESS':
			return {departments: state.departments.filter(department => department.appointment_id !== action.payload), loadingFlag: false};

		default:
			return {departments: state === undefined ? [] : state.departments, loadingFlag: false};
	}
};
