import React from 'react';
import MaskedInput from 'react-maskedinput';

import Button from '../Button/Button';

import styles from './AuthorizationForm.module.css';


const emptyState =  {
	omsNumber: '',
	birthDate: '',
	error: '',
};

const fieldIsEmpty = value => value === '' || value.includes('_');

export default class AuthorizationForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = emptyState;
	}

	clearForm() {
		this.setState(emptyState);
	}

	hasEmptyFields = () =>
		['omsNumber', 'birthDate'].some(field =>
			fieldIsEmpty(this.state[field])
	);

	formIsValid() {
		return !this.hasEmptyFields();
	}

	setFormError() {
		const errors = [
			{
				text: 'Необходимо заполнить все поля формы',
				matched: this.hasEmptyFields(),
			},
		];

		const currentError = errors.find(error => error.matched);

		if (currentError) {
			this.setState({ error: currentError.text });
		}
	}

	handleSubmit = event => {
		event.preventDefault();

		if (!this.formIsValid()) {
			this.setFormError();
			return;
		}

		this.props.handleSubmit({
			omsNumber: this.state.omsNumber,
			birthDate: this.state.birthDate,
		});

		this.clearForm();
	};

	handleInputChange = event => {
		this.setState({
			error: '',
			[event.target.name]: event.target.value,
		});
	};

	render() {
		return (
			<form onSubmit={this.handleSubmit} className={styles.authorizationForm}>
				<h2>Запись к врачу</h2>
				<div className={styles.fields}>
					<MaskedInput
						mask="1111111111111111"
						name="omsNumber"
						value={this.state.omsNumber}
						onChange={this.handleInputChange}
						placeholder="Номер полиса"
						className={styles.input}
					/>
          <MaskedInput
						mask="11/11/1111"
						name="birthDate"
						value={this.state.birthDate}
						onChange={this.handleInputChange}
						placeholder="Дата рождения"
						className={styles.input}
					/>
				</div>
				{this.state.error && (
						<div className={styles.errorWrapper}>{this.state.error}</div>
				)}
				<Button type="submit">Продолжить</Button>
			</form>
		);
	}
}
