const BASE_URL = "https://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"


function generateBodyForCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, date) {
		const start = new Date(Date.parse(date));
		const end = new Date(new Date(start).setDate(start.getDate()+1))
		return {
			"appointment": {
				"available_resource_id": String(availableResourceId),
				"complex_resource_id": String(complexResourceId),
				"start_time": start.toISOString().split('.')[0],
				"finish_time": end.toISOString().split('.')[0],
				"lpu_id": String(lpuId),
				"priority": 0,
				"reception_type_code_or_ldp_type_code": code,
			},
			"person": {
				"birth_date": birthDate,
				"oms_number": omsNumber,
			}
		};
};


function fetchCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, dates) {
	return Promise.all(dates.map(date =>
		fetch(
			BASE_URL+APPOINTMENT_URL,
			{
				method: 'POST',
				body: JSON.stringify(generateBodyForCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, date)),
				headers: {
					'Content-Type': 'application/json'
				},
			}
		)
	));
}

function fetchLoadAppointment(omsNumber, birthDate) {
  return fetch(
    BASE_URL+APPOINTMENT_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`,
    {
      method: 'GET',
      headers: {'Content-Type': 'application/json'},
    },
  );
};


function fetchDeleteAppointment(appointmentId, omsNumber, birthDate) {
	return fetch(
		BASE_URL+APPOINTMENT_URL+`/${appointmentId}?oms_number=${omsNumber}&birth_date=${birthDate}`,
		{
			method: 'DELETE',
			headers: {'Content-Type': 'application/json'},
		});
};





