import React from 'react';

import styles from './Schedule.module.css';

import AppointmentItem from '../AppointmentItem/AppointmentItem';

const BASE_URL = "https://localhost:5000"
const APPOINTMENT_URL = "/appointments"


export default class Appointment extends React.Component {
	constructor(props) {
		super(props);

		this.state = {appointments: []}
	}

	fetchAppointments(oms_number, birth_date) {
		const data = Object.assign({
			method: "get_specialities_info",
			params: {birth_date, oms_number},
		}, DEFAULT_DATA)

		return fetch(BASE_URL+DEPARTMENT_URL, {
										method: 'POST',
										body: JSON.stringify(data)
									 })
						.then(response => response.json())
						.then(response => response['result'])
						.then(response => this.setState({ ...this.state,
																						 departments: response,
																						}));
	}

	componentDidMount() {
		this.fetchAppointments(this.props.omsNumber, this.props.birthDate);
	}

  render() {
		return (
			<ui className={styles.appointments}>
				this.state.appointments.map(appointment => <AppointmentItem appointment={appointment}/>)
			</ui>
		);
	};
};
