import React, { Component, Fragment } from 'react';

import ScheduleCell from '../ScheduleCell/ScheduleCell';
import Button from '../Button/Button';

import styles from './ScheduleTable.module.css';


function days2weeks(days) {
  const weeks = [];

  for (let i = 0; i < days.length / 7; i++) {
    weeks[i] = []
    for (let j = 0; j <  7; j++) {
      weeks[i].push(days[i*7+j])
    }
  }

  return weeks
}


export default class ScheduleTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {dates: {}}

    this.handleClick = this.handleClick.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleClick(element, active) {
    this.setState({...this.state, dates: Object.assign(this.state.dates, {date: element['date']})})
    console.log(this.state);
  };

  handleSubmit() {
    console.log('fetch', this.state)
    // {
    //   "appointment": {
    //     "available_resource_id": "10064025",
    //     "complex_resource_id": "10059868",
    //     "finish_time": "2019-12-10T14:00:00Z",
    //     "lpu_id": "10000401",
    //     "priority": 0,
    //     "reception_type_code_or_ldp_type_code": "1863",
    //     "start_time": "2019-12-10T12:00:00Z"
    //   },
    //   "person": {
    //     "birth_date": "2019-12-08",
    //     "oms_number": "string"
    //   }
    // }
  }

  render() {
    const table = [];
    const weeks = days2weeks(this.props.schedule);

    for (const week of weeks){
      let row = [];
      for (const day of week) {
        row.push(<td><ScheduleCell handleClick={this.handleClick} cell={day}/></td>)
      }
      table.push(<tr>{row}</tr>)
    }

    return (
      <Fragment>
        <table className={styles.items}>
          <thead>
            <tr>
              <th>Понедельник</th>
              <th>Вторник</th>
              <th>Среда</th>
              <th>Четверг</th>
              <th>Пятница</th>
              <th>Суббота</th>
              <th>Воскресенье</th>
            </tr>
          </thead>
        <tbody>
          {table}
        </tbody>
      </table>
      <Button onClick={this.handleSubmit}>{'Встать в очередь'}</Button>
    </Fragment>
    )
  };
}
