import React from 'react';
import { NavLink } from 'react-router-dom';

import HeaderItem from '../HeaderItem/HeaderItem';

import styles from './Header.module.css';

import { connect } from 'react-redux';


class Header extends React.Component {
	stateFromProps() {
		return {links: [
			{url: '/authorization', text: "Авторизация", active: !this.props.authorizationFlag},
			{url: '/appointment/department', text: "Записатьcя", active: this.props.authorizationFlag},
			{url: '/appointments', text: "Мои записи", active: this.props.authorizationFlag},
			{url: '/help', text: "Помощь", active: true},
			{url: '/exit', text: "Выход", active: this.props.authorizationFlag},
		]};
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		if (prevProps.active !== this.props.active) {
			this.setState(this.stateFromProps(this.props));
		}
	}

	render() {
		return (
			<div className={styles.board}>
				{this.state.links.map(link =>
					link['active'] &&
					<NavLink to={link['url']} className={styles.link} activeClassName={styles.activeItem}> 
						<HeaderItem text={link['text']} />
				</NavLink>)}
			</div>
		);
	}
}

const mapStateToProps = state => {console.log('state: ', state); return {authorizationFlag: state.shedule.authorizationFlag}};

export { Header };

export default connect(mapStateToProps)(Header);
