import React, { Component, Fragment } from 'react';

import ScheduleCheckbox from '../ScheduleCheckbox/ScheduleCheckbox';
import Button from '../Button/Button';

import styles from './ScheduleTable.module.css';

function days2weeks(days) {
  const weeks = [];

  for (let i = 0; i < days.length / 7; i++) {
    weeks[i] = []
    for (let j = 0; j <  7; j++) {
      weeks[i].push(days[i*7+j])
    }
  }

  return weeks
}

export default ( { schedule } ) => {

    const table = [];
    const weeks = days2weeks(schedule);

    for (const week of weeks){
      let row = [];
      for (const day of week) {
        row.push(<td><ScheduleCheckbox cell={day}/></td>)
      }
      table.push(<tr>{row}</tr>)
    }

    return (
      <form>
        <table className={styles.items}>
          <thead>
            <tr>
              <th>Понедельник</th>
              <th>Вторник</th>
              <th>Среда</th>
              <th>Четверг</th>
              <th>Пятница</th>
              <th>Суббота</th>
              <th>Воскресенье</th>
            </tr>
          </thead>
        <tbody>
          {table}
        </tbody>
      </table>
    </form>
    )
  };
