import React from 'react';

import Tile from '../components/Tile/Tile'

import styles from './AppointmentPage.module.css'

import Spinner from 'react-bootstrap/Spinner'



const BASE_URL = "https://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"


export default class AppointmentPage extends React.Component{
	constructor(props) {
		super(props);
		this.state = {departments: [], loading: {flg: true}};
		this.handleDeleteAppointment = this.handleDeleteAppointment.bind(this);
		this.handleDeleteAllAppointment = this.handleDeleteAllAppointment.bind(this);
		this.handleAppointment = this.handleAppointment.bind(this);
	}

	fetchAppointment(omsNumber, birthDate) {
		return fetch(BASE_URL+APPOINTMENT_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`,
								 {headers: {'Content-Type': 'application/json'}})
						.then(response => response.json())
						.then(response => this.setState({...this.state, departments: response}))
            .catch(err => this.setState({...this.state, departments: []}))
            .finally(_ => this.setState({...this.state, loading: {flg: false}}));
  };

  fetchDeleteAppointment(appointmentId, omsNumber, birthDate) {
		return fetch(BASE_URL+APPOINTMENT_URL+`/${appointmentId}?oms_number=${omsNumber}&birth_date=${birthDate}`,
									{
										method: 'DELETE',
										headers: {'Content-Type': 'application/json'},
									});
	};

	handleAppointment() {
    this.setState({...this.state, loading: {flg: true}});
    this.fetchAppointment(localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'));
  }

	handleDeleteAppointment(appointmentId) {
		this.fetchDeleteAppointment(appointmentId, localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'))
		.then(()=>this.setState({...this.state, departments: this.state.departments.filter(department => department.appointment_id !== appointmentId)}));
	};

	handleDeleteAllAppointment(doctor) {
		this.state.departments.filter(department => department.available_resource_id === doctor).map(department =>
			this.fetchDeleteAppointment(department.appointment_id, localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'))
			.then(()=>this.setState({...this.state, departments: this.state.departments.filter(department => department.available_resource_id !== doctor)})));
	};

	componentDidMount() {
		this.handleAppointment();
	}

	render() {
		const loadPage = <div className={styles.loadPage} ><Spinner animation="border" variant="primary" /></div>;

		if (this.state.loading.flg) {
			return loadPage;
		}
		console.log(this.state)
		const doctors = [...new Set(this.state.departments.map(x => x.available_resource_id))];
		const emptyPage = <h2>{'У вас пока нет записей!'}</h2>;
		const page = (
			<div className={styles.page}>
			<ul className={styles.list}>
				{doctors
					.map(doctor => 
						<Tile
							key={doctor}
							handleDelete={() => this.handleDeleteAllAppointment(doctor)}
							handleDeleteItem={this.handleDeleteAppointment}
							appointments={this.state.departments.filter(x => x.available_resource_id === doctor)}
						/>)
				}
			</ul>
		</div>
		);

		return  this.state.departments.length === 0 ? emptyPage : page;
	};
};
