import {saveUserData} from '../authorization/actions'

const PROXY_URL = "https://proxy-q-doctor.herokuapp.com/api/emias/"
const SCHEDULE_URL = "schedule"


function getSchedule(omsNumber, birthDate) {
  return fetch(
    PROXY_URL+SCHEDULE_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`,
    {
      method: 'GET',
      headers: {'Content-Type': 'application/json'},
    },
  );
}


export const loadSchedule = payload => {
	return function(dispatch) {
    dispatch(loadScheduleAction);
    return getSchedule(...payload)
            .then(response => {
              if (response.status !== 200) {throw 'Ошибка авторизации'}
              return response
            })
            .then(response => response.json())
            .then(response => {dispatch(loadScheduleSuccess(response));  dispatch(saveUserData(payload))})
            .catch(() => dispatch(loadScheduleFailureAction));
	};
};

export const loadScheduleAction = {
	type: 'LOAD_SCHEDULE',
	payload: null,
};

export const loadScheduleFailureAction = {
	type: 'LOAD_SCHEDULE_FAILURE',
	payload: 'Авторизация завершилась с ошибкой',
};

export const loadScheduleSuccess = payload => {
  return ({
	type: 'LOAD_SCHEDULE_SUCCESS',
	payload: payload,
});}

export const closeBanner = {
  type: 'RESET_SCHEDULE',
  payload: null,
}
