import React, {Fragment} from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

// import { loadAppointment, addAppointment, deleteAppointment } from './redux/appointment/actions';
import { loadSchedule } from './redux/schedule/actions';



import NotFoundPage from './pages/NotFoundPage';
import ExitPage from './pages/ExitPage';
// import HelpPage from './pages/HelpPage';
import AppointmentPage from './pages/AppointmentPage';
import DepartmentPage from './pages/DepartmentPage';
import DoctorPage from './pages/DoctorPage';
import SchedulePage from './pages/SchedulePage';
import AuthorizationPage from './pages/AuthorizationPage';

import Header from './components/Header/Header';

import styles from './App.module.css';

import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';


import store from './redux/store/store';

localStorage.clear()


class App extends React.Component {
  constructor(props) {
    super(props);
	}

  renderAuthorizationUser = () => (
    <Fragment>
      <Route path="/appointment/department/:departmentId/doctor/:doctorId/schedule" component={SchedulePage} />
      <Route path="/appointment/department/:departmentId/doctor" component={DoctorPage} />
      <Route path="/appointment/department" component={DepartmentPage} />
      <Route path="/appointments" component={AppointmentPage} />
      <Route path="/exit" component={ExitPage} />
    </Fragment>
  );

  renderNotAuthorizationUser = () => (
    <Fragment>
      <Route exact path="/" component={AuthorizationPage} />
      <Route path="/authorization" component={AuthorizationPage} />
    </Fragment>
  );

  renderRoute = () => (
    <Switch>
      {store.getState().schedule.authorizationFlag ? this.renderAuthorizationUser() : this.renderNotAuthorizationUser()}
      {/* <Route path="/help" component={HelpPage} /> */}
      <Route path="/" component={NotFoundPage} />
    </Switch>
  );

  renderPage = () => (
    <div className={styles.main}>
      {this.renderRoute()}
    </div>
  );

  renderLoadPage = () =>  (
    <div className={styles.loadPage} ><Spinner animation="border" variant="primary" /></div>
  );

  renderErrorPage = (text) => (
    //onClose={() => this.setState({err: ''})}
    <Alert variant="danger" dismissible>
      <Alert.Heading>Error</Alert.Heading>
      <p>{text}</p>
    </Alert>
  )

  render() {    
    return (
      <Router>
        <div className={styles.App}>
          <Header />
          {this.props.loadingFlag ? this.renderLoadPage() : ( store.getState().schedule.authorizationFlag ? this.renderPage() : this.renderErrorPage(this.props.textError) )}
        </div>
      </Router>
    );
  };
};


const mapStateToProps = state => {
                                  return { schedule: state.schedule,
                                  // authorizationFlag: Boolean(state.schedule.schedule.length),
                                  textError: state.schedule.textError,
                                  loadingFlag: state.schedule.loadingFlag}};
const mapDispatchToProps = dispatch => ({
	loadSchedule: payload => dispatch(loadSchedule(payload)),
});


export { App };

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(App);
