import React from 'react';

import Tile from '../components/Tile/Tile'

import styles from './AppointmentPage.module.css'


const BASE_URL = "http://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"


export default class AppointmentPage extends React.Component {
	constructor(props) {
		super(props);

		this.state = {departments: [], doctors: []};
		this.handleDeleteAllAppointment.bind(this);
		this.handleDeleteAppointment.bind(this);
	}

	fetchDeleteAppointment(appointmentId, omsNumber, birthDate) {
		return fetch(BASE_URL+APPOINTMENT_URL+`/${appointmentId}?oms_number=${omsNumber}&birth_date=${birthDate}`,
									{
										method: 'DELETE'
									})
						.then(this.setState({departments: this.state.departments.filter(department => department.appointment_id !== appointmentId)}));
	};

	handleDeleteAppointment(appointmentId) {
		this.fetchDeleteAppointment(appointmentId, localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'));
	};

	handleDeleteAllAppointment(doctor) {
		this.state.departments.filter(department => department.available_resource_id === doctor).map(department =>
			this.fetchDeleteAppointment(department.appointment_id, localStorage.getItem('omsNumber'), localStorage.getItem('birthDate')));
	};

	fetchAppointment(omsNumber, birthDate) {
		return fetch(BASE_URL+APPOINTMENT_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`)
						.then(response => response.json())
						.then(response => this.setState({departments: response}))
						.catch(err => this.setState({departments: []}));
	}

	componentWillMount() {
		this.fetchAppointment(localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'));
	}

	render() {
		if (localStorage.getItem('schedule').length !== 0) {
			if (this.state.departments.length === 0) {
				return <h2>Идет загрузка</h2>;
			}

			const doctors = [...new Set(this.state.departments.map(x => x.available_resource_id))];
			return (
				<div className={styles.page}>
					<ul className={styles.list}>
						{doctors
						 .map(doctor => 
								<Tile
									key={doctor}
									handleDelete={() => this.handleDeleteAllAppointment(doctor)}
									handleDeleteItem={this.handleDeleteAppointment}
									appointments={this.state.departments.filter(x => x.available_resource_id === doctor)}
								/>)
						}
					</ul>
				</div>
			);
		}

		return <h2>Сначала авторизуйтесь!</h2>;
};
}
