import React, { Component, Fragment } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import NotFoundPage from './pages/NotFoundPage';
import HelloPage from './pages/HelloPage';
import HelpPage from './pages/HelpPage';
import AppointmentPage from './pages/AppointmentPage';
import DepartmentPage from './pages/DepartmentPage';
import DoctorPage from './pages/DoctorPage';
import SchedulePage from './pages/SchedulePage';
import AuthorizationPage from './pages/AuthorizationPage';

import Header from './components/Header/Header';

import styles from './App.module.css';


function formatBirthDate(date) {
	const [day, month, year] = date.split('/');
	return `${year}-${month}-${day}`
}

const items = {
	authorization: "Авторизация",
	'appointment/department': "Записатьcя",
	appointments: "Мои записи",
	help: "Помощь",
}


const PROXY_URL = "https://proxy-q-doctor.herokuapp.com/api/emias/"
const SCHEDULE_URL = "schedule"



// localStorage.clear()

class App extends React.Component {
  fetchSchedule(omsNumber, birthDate) {
    console.log(omsNumber, birthDate)
    return fetch(PROXY_URL+SCHEDULE_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`,
                 {headers: {'Content-Type': 'application/json'}})
						.then(response => response.json())
            .then(response => localStorage.setItem('schedule', JSON.stringify(response)))
            // .catch(err => localStorage.setItem('schedule', JSON.stringify({})));
  }

  handleAuthorization = person => {
    localStorage.setItem('birthDate', formatBirthDate(person.birthDate));
    localStorage.setItem('omsNumber', person.omsNumber);
    this.fetchSchedule(localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'));
  };

  render() {
    console.log(localStorage)
    return (
      <div className={styles.App}>
        <Router>

            <Header links={items} />

            <div className={styles.main}>
              <Switch>

                <Route exact path="/" component={HelloPage} />

                <Route path="/authorization">
                  <AuthorizationPage handleSubmit={this.handleAuthorization} />
                </Route>

                <Route path="/appointment/department/:departmentId/doctor/:doctorId/schedule" component={SchedulePage} />

                <Route path="/appointment/department/:departmentId/doctor" component={DoctorPage} />

                <Route path="/appointment/department">
                  <DepartmentPage />
                </Route>

                <Route path="/appointments">
                  <AppointmentPage />
                </Route>

                <Route path="/help" component={HelpPage} />

                <Route path="/" component={NotFoundPage} />

              </Switch>
            </div>

        </Router>
     </div>
    );
  };
};

export default App;
