import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

import { closeBanner } from './redux/schedule/actions';

import NotFoundPage from './pages/NotFoundPage';
import ExitPage from './pages/ExitPage';
// import HelpPage from './pages/HelpPage';
import AppointmentPage from './pages/AppointmentPage';
import DepartmentPage from './pages/DepartmentPage';
import DoctorPage from './pages/DoctorPage';
import SchedulePage from './pages/SchedulePage';
import AuthorizationPage from './pages/AuthorizationPage';

import Header from './components/Header/Header';

import styles from './App.module.css';

import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';


class App extends React.Component {
  renderAuthorizationUser = () => ([
      <Route key={1} path="/appointment/department/:departmentId/doctor/:doctorId/schedule" component={SchedulePage} />,
      <Route key={2} path="/appointment/department/:departmentId/doctor" component={DoctorPage} />,
      <Route key={3} path="/appointment/department" component={DepartmentPage} />,
      <Route key={4} path="/appointments" component={AppointmentPage} />,
      <Route key={5} path="/exit" component={ExitPage} />,
      <Route key={6} path="/" component={AppointmentPage} />,
  ]);

  renderNotAuthorizationUser = () => ([
      <Route key={1} path="/" component={AuthorizationPage} />,
  ]);

  renderRoute = () => (
    <Switch>
      {this.props.authorizationFlag ? this.renderAuthorizationUser() : this.renderNotAuthorizationUser()}
      {/* <Route path="/help" component={HelpPage} /> */}
      <Route path="/" component={NotFoundPage} />
    </Switch>
  );

  renderPage = () => (
    <div className={styles.main}>
      {this.renderRoute()}
    </div>
  );

  renderLoadPage = () =>  (
    <div className={styles.loadPage} ><Spinner animation="border" variant="primary" /></div>
  );

  renderFailurePage = () => (
    <Alert onClose={() => this.props.closeBanner()} variant="danger" dismissible>
      <Alert.Heading>Error</Alert.Heading>
      <p>{this.props.textError}</p>
    </Alert>
  )

  // renderSuccess = () => (
  //   <Alert onClose={() => {window.location='/appointment/department'}} variant="success" dismissible>
  //     <Alert.Heading>Success</Alert.Heading>
  //     <p>{this.props.textError}</p>
  //   </Alert>
  // )

  render() {
    return (
      <Router>
        <div className={styles.App}>
          {this.props.authorizationFlag && <Header />}
          {this.props.loadingFlag ? this.renderLoadPage() : ( this.props.textError ? this.renderFailurePage() :  this.renderPage() )}
          {/* {this.props.authorizationFlag && this.renderSuccess()} */}
        </div>
      </Router>
    );
  }
}


const mapStateToProps = state => {return (
  { 
    textError: state.schedule.textError,
    loadingFlag: state.schedule.loadingFlag,
    authorizationFlag: state.authorization.authorizationFlag
  }
)};

const mapDispatchToProps = dispatch => ({
	closeBanner: () => dispatch(closeBanner),
});


export { App };

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(App);
