import React, { Component, Fragment } from 'react';

import ScheduleCell from '../ScheduleCell/ScheduleCell';
import Button from '../Button/Button';

import styles from './ScheduleTable.module.css';


const BASE_URL = "https://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"


function days2weeks(days) {
  const weeks = [];

  for (let i = 0; i < days.length / 7; i++) {
    weeks[i] = []
    for (let j = 0; j <  7; j++) {
      weeks[i].push(days[i*7+j])
    }
  }

  return weeks
}


export default class ScheduleTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};

    this.handleClick = this.handleClick.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  fetchCreateAppointment(appointments) {
    Promise.all(appointments.map(appointment =>
      fetch(BASE_URL+APPOINTMENT_URL,
        {
          method: 'POST',
          body: JSON.stringify(appointment),
          headers: {
            'Content-Type': 'application/json'
          },
        }
      )
    ));
  }

  handleClick(element, active) {
    console.log('click', this.state)
    this.setState(Object.assign(this.state, {[element.date]: active}))
  };

  handleSubmit() {
    console.log(this.state)
    const appointments = [];
    for (let key of Object.keys(this.state).filter(key => this.state[key])) {
      const start = new Date(Date.parse(key));
      const end = new Date(new Date(start).setDate(start.getDate()+1))
      appointments.push({
        "appointment": {
          "available_resource_id": String(this.props.availableResourceId),
          "complex_resource_id": String(this.props.complexResourceId),
          "start_time": start.toISOString().split('.')[0],
          "finish_time": end.toISOString().split('.')[0],
          "lpu_id": String(this.props.lpuId),
          "priority": 0,
          "reception_type_code_or_ldp_type_code": this.props.code,
        },
        "person": {
          "birth_date": localStorage.getItem('birthDate'),
          "oms_number": localStorage.getItem('omsNumber'),
        }
      })
    }
    // this.setState(Object.keys(this.state))
    this.fetchCreateAppointment(appointments)    
    console.log(this.state)
  }

  render() {
    console.log(this.state)
    const table = [];
    const weeks = days2weeks(this.props.schedule);

    for (const week of weeks){
      let row = [];
      for (const day of week) {
        row.push(<td><ScheduleCell handleClick={this.handleClick} active={Boolean(this.state[day.date])} cell={day}/></td>)
      }
      table.push(<tr>{row}</tr>)
    }

    return (
      <div className={styles.schedule}>
        <table className={styles.table}>
          <thead>
            <tr>
              <th>Понедельник</th>
              <th>Вторник</th>
              <th>Среда</th>
              <th>Четверг</th>
              <th>Пятница</th>
              <th>Суббота</th>
              <th>Воскресенье</th>
            </tr>
          </thead>
        <tbody>
          {table}
        </tbody>
      </table>
      <Button type='submit' onClick={this.handleSubmit}>{'Встать в очередь'}</Button>
    </div>
    )
  };
}
