const BASE_URL = 'https://back-q-doctor.herokuapp.com/api'
const APPOINTMENT_URL = '/people/appointments'


function generateBodyForCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, date) {
		const start = new Date(Date.parse(date));
		const end = new Date(new Date(start).setDate(start.getDate()+1))
		return {
			'appointment': {
				'available_resource_id': String(availableResourceId),
				'complex_resource_id': String(complexResourceId),
				'start_time': start.toISOString().split('.')[0],
				'finish_time': end.toISOString().split('.')[0],
				'lpu_id': String(lpuId),
				'priority': 0,
				'reception_type_code_or_ldp_type_code': code,
			},
			'person': {
				'birth_date': birthDate,
				'oms_number': omsNumber,
			}
		};
}


function fetchCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, dates) {
	return Promise.all(dates.map(date =>
		fetch(
			BASE_URL+APPOINTMENT_URL,
			{
				method: 'POST',
				body: JSON.stringify(generateBodyForCreateAppointment(omsNumber, birthDate, availableResourceId, complexResourceId, lpuId, code, date)),
				headers: {
					'Content-Type': 'application/json'
				},
			}
		)
	));
}

function fetchLoadAppointment(omsNumber, birthDate) {
  return fetch(
    BASE_URL+APPOINTMENT_URL+`?oms_number=${omsNumber}&birth_date=${birthDate}`,
    {
      method: 'GET',
      headers: {'Content-Type': 'application/json'},
    },
  );
}


function fetchDeleteAppointment(appointmentId, omsNumber, birthDate) {
	return fetch(
		BASE_URL+APPOINTMENT_URL+`/${appointmentId}?oms_number=${omsNumber}&birth_date=${birthDate}`,
		{
			method: 'DELETE',
			headers: {'Content-Type': 'application/json'},
		});
}


export const createAppointment = payload => {
	return function(dispatch) {
		dispatch(createAppointmentAction)
		return fetchCreateAppointment(...payload)
			.then(() => dispatch(createAppointmentSuccess));
	};
};

export const loadAppointment = payload => {
	return function(dispatch) {
    dispatch(loadAppointmentAction);
		return fetchLoadAppointment(...payload)
			.then(response => response.json())
			.then(response => dispatch(loadAppointmentSuccess(response)));
	};
};

export const deleteAppointment = payload => {
	return function(dispatch) {
		dispatch(deleteAppointmentAction);
		return Promise.all(payload[0].map(id => fetchDeleteAppointment(id, payload[1], payload[2])))
						.then(() => dispatch(deleteAppointmentSuccess(payload[0])))
						.catch(() => dispatch(deleteAppointmentFailure));
	};
};

export const createAppointmentAction = {
	type: 'CREATE_APPOINTMENT',
	payload: null,
};

export const createAppointmentSuccess = ({
	type: 'CREATE_APPOINTMENT_SUCCESS',
	payload: null,
});

export const loadAppointmentAction = {
	type: 'LOAD_APPOINTMENT',
	payload: null,
};

export const loadAppointmentSuccess = payload => ({
	type: 'LOAD_APPOINTMENT_SUCCESS',
	payload: payload,
});

export const deleteAppointmentAction = {
	type: 'DELETE_APPOINTMENT',
	payload: null,
};

export const deleteAppointmentSuccess = payload => ({
	type: 'DELETE_APPOINTMENT_SUCCESS',
	payload: payload,
});

export const deleteAppointmentFailure = {
	type: 'DELETE_APPOINTMENT_FAILURE',
	payload: null,
};
