import React from 'react';

import styles from './Appointment.module.css';

import AppointmentItem from '../AppointmentItem/AppointmentItem';

const BASE_URL = "http://back-q-doctor.herokuapp.com/api"
const APPOINTMENT_URL = "/people/appointments"

const PROXY_URL = "https://proxy-q-doctor.herokuapp.com/api/emias/"
const DOCTOR_NAME_URL = "doctor_name"


// const available_resource_id = doctor['lpuRoomsInfo']['lpuRoomView'][0]['availableResourceId']
// const lpu_id = doctor['lpuRoomsInfo']['lpuRoomView'][0]['lpuId']

export default class Appointment extends React.Component {
	constructor(props) {
		super(props);

		this.state = {appointments: []}
	}

	fetchAppointments(oms_number, birth_date) {
		return fetch(BASE_URL+APPOINTMENT_URL+`?oms_number=${oms_number}&birth_date=${birth_date}`)
						.then(response => response.json())
						// .then(response => this.setState({ ...this.state, appointments: response}));
	}

	fetchDoctorName(available_resource_id, lpu_id, other) {
		return fetch(BASE_URL+DOCTOR_NAME_URL+`?available_resource_id=${available_resource_id}&lpu_id=${lpu_id}`)
						.then(response => response.json())
						// .then(response => this.setState({ ...this.state
						// 																	this.state.appointments.push(Object.assign(other, response)) });
	}

	componentDidMount() {
		this.fetchAppointments(localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'))
			.then(appointments => appointments.map(appointment => fetchDoctorName(appointment['lpu_id'], appointment['available_resource_id'], other)));
	}

  render() {
		return (
			<ul className={styles.items}>
				{this.state.appointments.map(appointment => <AppointmentItem {...appointment}/>)}
			</ul>
		);
	};
};
