import React from 'react';
import { NavLink } from 'react-router-dom';

import HeaderItem from '../HeaderItem/HeaderItem';

import styles from './Header.module.css';


export default class Header extends React.Component {
	constructor(props) {
		super(props)

		this.state = this.stateFromProps(this.props);
	}

	stateFromProps(props) {
		return {links: [
			{url: '/authorization', text: "Авторизация", active: true},
			{url: '/appointment/department', text: "Записатьcя", active: props.active},
			{url: '/appointments', text: "Мои записи", active: props.active},
			{url: '/help', text: "Помощь", active: true},
			{url: '/', text: "Выход", active: props.active},
		]};
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		if (prevProps.active !== this.props.active) {
			this.setState(this.stateFromProps(this.props));
		}
	}

	render() {
		console.log(this.state.links)
		return (
			<div className={styles.board}>
				{this.state.links.map(link =>
					link['active'] &&
					<NavLink to={link['url']} className={styles.link} activeClassName={styles.activeItem}> 
						<HeaderItem text={link['text']} />
				</NavLink>)}
			</div>
		);
	}
}
