import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

// import { loadAppointment, addAppointment, deleteAppointment } from './redux/appointment/actions';
import { loadSchedule } from './redux/shedule/actions';

import NotFoundPage from './pages/NotFoundPage';
import ExitPage from './pages/ExitPage';
import HelpPage from './pages/HelpPage';
import AppointmentPage from './pages/AppointmentPage';
import DepartmentPage from './pages/DepartmentPage';
import DoctorPage from './pages/DoctorPage';
import SchedulePage from './pages/SchedulePage';
import AuthorizationPage from './pages/AuthorizationPage';

import Header from './components/Header/Header';

import styles from './App.module.css';

import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';



// localStorage.clear()


class App extends React.Component {
  constructor(props) {
    super(props);
		this.state = {loadingFlaf: false, err: ''};
	}

  fetchSchedule = (omsNumber, birthDate) => this.props.loadSchedule(omsNumber, birthDate);

  handleAuthorization = person => {
    localStorage.setItem('birthDate', formatBirthDate(person.birthDate));
		localStorage.setItem('omsNumber', person.omsNumber);
		this.setState({loadingFlag: true});
    this.fetchSchedule(localStorage.getItem('omsNumber'), localStorage.getItem('birthDate'));
  };

  renderPage = () => (
    <div className={styles.main}>
      <Switch>
        <Route exact path="/">
          <AuthorizationPage handleSubmit={this.handleAuthorization} />
        </Route>
        <Route path="/authorization">
          <AuthorizationPage handleSubmit={this.handleAuthorization} />
        </Route>
        {localStorage.getItem('authFlag') && <Route path="/appointment/department/:departmentId/doctor/:doctorId/schedule" component={SchedulePage} />}
        {localStorage.getItem('authFlag') && <Route path="/appointment/department/:departmentId/doctor" component={DoctorPage} /> }
        {localStorage.getItem('authFlag') &&<Route path="/appointment/department" component={DepartmentPage} /> }
        {localStorage.getItem('authFlag') &&<Route path="/appointments" component={AppointmentPage} /> }
        <Route path="/help" component={HelpPage} />
        {localStorage.getItem('authFlag') && <Route path="/exit" component={ExitPage} /> }

        <Route path="/" component={NotFoundPage} />
      </Switch>
    </div>
  );

  renderLoadPage = () =>  (
    <div className={styles.loadPage} ><Spinner animation="border" variant="primary" /></div>
  );

  renderErrorPage = () => (
    <Alert variant="danger" onClose={() => this.setState({err: ''})} dismissible>
      <Alert.Heading>Error</Alert.Heading>
      <p>{this.state.err}</p>
    </Alert>
  )

  render() {
    return (
      <Router>
        <div className={styles.App}>
          <Header handleSubmit={this.handleAuthorization} active={Boolean(localStorage.getItem('schedule'))}/>
          {this.state.err ? this.renderErrorPage() : (this.state.loadingFlag ? this.renderLoadPage() : this.renderPage())}
        </div>
      </Router>
    );
  };
};


const mapStateToProps = state => ({ schedule: state.schedule });
const mapDispatchToProps = dispatch => ({
	loadAccounts: payload => dispatch(loadSchedule(payload)),
});


export { App };

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(App);
