import React from 'react';

import styles from './Tile.module.css';

import Action from '../Action/Action'

import TileItem from '../TileItem/TileItem';

import store from '../../redux/store/store'


function getMetadataDoctor(departments, appointment) {
	const metadataDoctor = Object.values(departments).flatMap(val => val['doctors'][appointment.available_resource_id]).filter(x => Boolean(x))[0];
	return metadataDoctor;
}


export default ({ appointments, handleDelete, handleDeleteItem }) => {
	const metadataDoctor = getMetadataDoctor(store.getState().schedule.schedule.departments, appointments[0])
	console.log('appTile', appointments)
	const departments = store.getState().schedule.schedule.departments;
	const doctors = Object.values(departments).map(x => x['doctors'])  //Object.assign(...);
	const metaDoctor = doctors[appointments[0].available_resource_id];
	console.log('TILE', departments, doctors, metaDoctor)
	return (
		<li className={styles.tile}>
			<div className={styles.header}>
				<h3>{metaDoctor.specialityName}</h3>
				<h4>{metaDoctor.firstName + '\t' + metaDoctor.lastName}</h4>
				<Action type='delete' onClick={handleDelete} />
			</div>
			<ul className={styles.list}>
				{appointments.map(record => 
					<TileItem
						key={record.appointment_id}
						{...record}
						handleSubmit={() => handleDeleteItem(record.appointment_id)} 
					/>
				)}
			</ul>
		</li>
	);
};
